package com.nanami.android.blackhistory.fragment.list;import android.support.annotation.NonNull;import android.support.annotation.Nullable;import android.widget.ListView;import com.nanami.android.blackhistory.adapter.TweetAdapter;import com.nanami.android.blackhistory.event.TwitterFavoriteEvent;import com.squareup.otto.Subscribe;import java.util.List;import twitter4j.Status;import twitter4j.Twitter;import twitter4j.TwitterException;/** * Created by TEJNEK on 2016/01/19. */final public class FavoriteStreamFragment extends CommonStreamFragment{    @Nullable    public static FavoriteStreamFragment newInstance(@NonNull Long userId){        if (2 > userId) return null;        FavoriteStreamFragment fragment = new FavoriteStreamFragment();        fragment.setArguments(userId, TimelineListType.Favorites);        return fragment;    }    @Override    public void response(ListView listView, TweetAdapter adapter, @NonNull List<Status> result) {        insertAllTweet(result, true);    }    @Nullable    @Override    public List<Status> call(Twitter twitter) throws TwitterException {        return twitter.getFavorites();    }    @Subscribe    public void OnTwitterFavoriteEvent(final TwitterFavoriteEvent event){        if(event.getUserId() != getUserId()) return;        insertTweet(event.getFavoritedStatus());    }}